CREATE OR REPLACE PROCEDURE pls_atualiza_item_selec_exec ( nr_seq_exec_item_lote_p bigint, nr_seq_exec_lote_p bigint, qt_item_atualizado_p bigint, cd_medico_p text, nm_usuario_p text, DS_RETORNO_P INOUT text) AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN

/* Obter regra de execução total dos itens */

ds_retorno_w	:= substr(pls_obter_se_regra_exec_total(nr_seq_exec_item_lote_p, nr_seq_exec_lote_p),1,2000);

if ( ds_retorno_w = '' or coalesce(ds_retorno_w::text, '') = '') then
	update	pls_itens_lote_execucao
	set	ie_executar 	 	= 'S',
		qt_item_exec 	 	= qt_item_atualizado_p,
		nm_usuario	 	= nm_usuario_p,
		dt_atualizacao	 	= clock_timestamp(),
		cd_medico_executor 	= cd_medico_p,
		ds_observacao 	 	= ds_observacao||' - PLS_ATUALIZA_ITEM_SELEC_EXEC: '||clock_timestamp()||' Qt: '|| qt_item_atualizado_p
	where	nr_sequencia	 	= nr_seq_exec_item_lote_p;
	commit;
end if;

ds_retorno_p := ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
