CREATE OR REPLACE PROCEDURE pls_atualiza_itens_exec_lote ( nr_seq_exec_item_p bigint, qt_proc_mat_exec_p bigint, cd_medico_executor_p text, nm_usuario_p text, ie_tipo_atendimento_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Atualizar itens na execução de requisição por lote
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ie_tipo_atendimento_w	pls_itens_lote_execucao.ie_tipo_atendimento%type;


BEGIN

select	CASE WHEN ie_tipo_guia=2 THEN  ie_tipo_atendimento_p  ELSE null END
into STRICT	ie_tipo_atendimento_w
from	pls_itens_lote_execucao
where	nr_sequencia	 	= nr_seq_exec_item_p;

update	pls_itens_lote_execucao
set	ie_executar 		= 'S',
	qt_item_exec 		= coalesce(qt_proc_mat_exec_p, qt_item_exec),
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp(),
	cd_medico_executor	= cd_medico_executor_p,
	ds_observacao 	 	= ds_observacao||' - PLS_ATUALIZA_ITENS_EXEC_LOTE: '||clock_timestamp()||' Qt: '||qt_proc_mat_exec_p,
	ie_tipo_atendimento	= ie_tipo_atendimento_w
where	nr_sequencia	 	= nr_seq_exec_item_p;

commit;

end;
$body$
