CREATE OR REPLACE PROCEDURE pls_atualiza_lib_composicao ( nr_seq_composicao_p pls_pacote_composicao.nr_sequencia%type, ie_acao_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

ie_acao_p:

L = Liberar
D = Desfazer liberação.

Alterações
------------------------------------------------------------------------------------------------------------------
jjung OS 614334 25/07/2013

Alteração:	Adicionado às atualizações o campo NM_USUARIO_LIBERACAO.

Motivo:	Foi identificado que o mesmo não estava sendo gravado e nem exibido na tela.
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (ie_acao_p = 'L') then

	update	pls_pacote_composicao
	set	dt_liberacao 		= clock_timestamp(),
		nm_usuario_liberacao	= nm_usuario_p,
		dt_atualizacao_nrec	= dt_atualizacao,
		nm_usuario_nrec		= nm_usuario,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao  	= clock_timestamp()
	where	nr_sequencia 		= nr_seq_composicao_p;

elsif (ie_acao_p = 'D') then

	update	pls_pacote_composicao
	set	dt_liberacao 		 = NULL,
		nm_usuario_liberacao 	 = NULL,
		dt_atualizacao_nrec	= dt_atualizacao,
		nm_usuario_nrec		= nm_usuario,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_composicao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
