CREATE OR REPLACE PROCEDURE pls_atualiza_nome_busca_guia ( cd_pessoa_fisica_p text, cd_cgc_p text, ds_razao_social_p text, nm_fantasia_p text, nm_pessoa_fisica_p text, nm_guerra_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Atualizar o NM_BUSCA_GUIA_MEDICO nas tabelas PLS_PRESTADOR  e PLS_PRESTADOR_MEDICO
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nm_medico_ou_prestador_w	varchar(2000);
nm_aux_w			varchar(2000);

C01 CURSOR FOR
	/* Atualizar quando trigger da pessoa jurídica */

	SELECT	nr_sequencia,
		cd_cgc cd_pf_pj,
		null ie_prefixo_guia_medico,
		ds_razao_social_p nm_prestador,
		nm_fantasia_p nm_usual,
		nm_interno,
		nm_adic_guia_medico
	from	pls_prestador
	where	cd_cgc			= cd_cgc_p
	and	(cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '')
	
union

	/* Atualizar quando trigger da pessoa física */

	SELECT	nr_sequencia,
		cd_pessoa_fisica cd_pf_pj,
		ie_prefixo_guia_medico,
		nm_pessoa_fisica_p nm_prestador,
		nm_guerra_p nm_usual,
		nm_interno,
		nm_adic_guia_medico
	from	pls_prestador
	where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and 	(cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '');

C02 CURSOR FOR
	SELECT	nr_sequencia,
		cd_medico,
		nm_pessoa_fisica_p nm_medico,
		nm_guerra_p nm_guerra
	from	pls_prestador_medico
	where	cd_medico	 	= cd_pessoa_fisica_p;
BEGIN

for r_c01_w in c01 loop
	begin
	if (C01%rowCount > 0) then
		nm_medico_ou_prestador_w	:= pls_obter_nome_busca_guia(	r_c01_w.ie_prefixo_guia_medico,
										r_c01_w.nm_prestador,
										r_c01_w.nm_usual,
										r_c01_w.nm_interno,
										r_c01_w.nm_adic_guia_medico);

		update	pls_prestador
		set	nm_busca_guia_medico	= nm_medico_ou_prestador_w
		where	nr_sequencia	= r_c01_w.nr_sequencia;
	end if;
	end;
end loop;

for r_c02_w in c02 loop
	begin
		if (C02%rowCount > 0) then

			nm_medico_ou_prestador_w	:= pls_obter_nome_busca_guia(	null,
											r_c02_w.nm_medico,
											r_c02_w.nm_guerra,
											null,
											null);
			update	pls_prestador_medico
			set	nm_busca_guia_medico	= nm_medico_ou_prestador_w
			where	nr_sequencia		= r_c02_w.nr_sequencia;
		end if;
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
