CREATE OR REPLACE PROCEDURE pls_atualiza_oco_analise_pos ( nr_seq_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_conta_proc_w	bigint;
nr_seq_conta_mat_w	bigint;
nr_sequencia_w		bigint;--sequencia da pls_analise_conta _tem
C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_mat
	where	nr_seq_conta = nr_seq_conta_p;

C03 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_conta_item
	where	nr_seq_conta_proc = nr_seq_conta_proc_w;

C04 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_conta_item
	where	nr_seq_conta_mat = nr_seq_conta_mat_w;



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C03;
	loop
	fetch C03 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		update 	pls_analise_conta_item
		set 	ie_status_faturamento	= 'L'
		where 	nr_sequencia 		= nr_sequencia_w
		and	nr_seq_conta_proc 	= nr_seq_conta_proc_w
		and 	ie_situacao 		= 'I';
		end;
	end loop;
	close C03;

	end;
end loop;
close C01;

open C02;
loop
