		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C04 */
		begin
		update 	pls_analise_conta_item
		set 	ie_status_faturamento	= 'L'
		where 	nr_sequencia 		= nr_sequencia_w
		and	nr_seq_conta_mat	= nr_seq_conta_mat_w
		and 	ie_situacao = 'I';
		end;
	end loop;
	close C04;

	end;
end loop;
close C02;

end;
