CREATE OR REPLACE PROCEDURE pls_atualiza_ordem_grupos ( nr_sequencia_p bigint, nr_seq_ordem_p bigint, nr_seq_analise_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_ordem_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_ordem
	from	pls_auditoria_conta_grupo
	where	nr_seq_analise	= nr_seq_analise_p
	and	nr_seq_ordem	= nr_seq_ordem_p
	and	nr_sequencia	<> nr_sequencia_p; --Muito importante para não entrar em loop <<<< NÃO REMOVER <<< NÃO ACRESCENTAR CLAUSALA "OR"
BEGIN

update	pls_auditoria_conta_grupo
set	nr_seq_ordem	= nr_seq_ordem_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia   = nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_ordem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL pls_atualiza_ordem_grupos(nr_sequencia_w, nr_seq_ordem_w + 1, nr_seq_analise_p,
				  cd_estabelecimento_p, nm_usuario_p);

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
