CREATE OR REPLACE PROCEDURE pls_atualiza_ordem_regra_reemb ( nr_sequencia_p pls_regra_reembolso.nr_sequencia%type) AS $body$
DECLARE


nr_seq_ordem_exec_w	pls_regra_reembolso.nr_sequencia%type;


BEGIN

	nr_seq_ordem_exec_w := pls_gerenciar_reembolso_pck.obter_ordem_sugerida_regra(nr_sequencia_p);

	update	pls_regra_reembolso
	set 	nr_ordem_execucao = coalesce(nr_seq_ordem_exec_w,0)
	where 	nr_sequencia = nr_sequencia_p;


commit;

end;
