SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualiza_proc_guia_web ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Atualizar os procedimentos das autorizações de intercâmbio.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_pacote_w			pls_pacote.nr_sequencia%type;
ie_ident_pacote_ptu_w		pls_param_intercambio_scs.ie_ident_pacote_ptu%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p;

BEGIN

select 	coalesce(ie_ident_pacote_ptu, 'N')
into STRICT	ie_ident_pacote_ptu_w
from	pls_param_intercambio_scs;

if (ie_ident_pacote_ptu_w = 'S') then
	for r_c01_w in c01 loop
		begin
			select	max(nr_sequencia)
			into STRICT	nr_seq_pacote_w
			from	pls_pacote
			where	cd_procedimento		= r_c01_w.cd_procedimento
			and	ie_origem_proced	= r_c01_w.ie_origem_proced
			and	ie_situacao		= 'A';

			if (nr_seq_pacote_w IS NOT NULL AND nr_seq_pacote_w::text <> '') then
				update	pls_guia_plano_proc
				set	ie_pacote_ptu	= 'S',
					nr_seq_pacote	= nr_seq_pacote_w
				where	nr_sequencia	= r_c01_w.nr_sequencia;
			end if;
		end;
	end loop;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
