CREATE OR REPLACE PROCEDURE pls_atualiza_result_contrato ( nr_seq_contrato_p bigint, dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, pr_desp_expediente_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_mes_referencia_w	timestamp;
vl_mensalidade_w	double precision	:= 0;
vl_despesa_w		double precision	:= 0;

C01 CURSOR FOR
	SELECT	trunc(dt_mes_referencia, 'month'),
		sum(coalesce(CASE WHEN ie_tipo_valor=1 THEN vl_resultado END ,0)) vl_mensalidade,
		sum(coalesce(CASE WHEN ie_tipo_valor=1 THEN 0  ELSE vl_resultado END ,0)) vl_despesa
	from	pls_resultado
	where	trunc(dt_mes_referencia, 'month') between trunc(dt_periodo_inicial_p, 'month') and trunc(dt_periodo_final_p, 'month')
	group by trunc(dt_mes_referencia, 'month');


BEGIN

open C01;
loop
fetch C01 into
	dt_mes_referencia_w,
	vl_mensalidade_w,
	vl_despesa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_resultado
	set	pr_desp_expediente			= CASE WHEN pr_desp_expediente_p=0 THEN pr_desp_expediente  ELSE pr_desp_expediente_p END
	where	trunc(dt_mes_referencia, 'month')	= dt_mes_referencia_w
	and	nr_seq_contrato				= nr_seq_contrato_p;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
