CREATE OR REPLACE PROCEDURE pls_atualiza_sit_w_an_glo_ocor ( nr_sequencia_p w_pls_analise_glosa_ocor.nr_sequencia%type, nr_seq_analise_p w_pls_analise_glosa_ocor.nr_seq_analise%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/*
  Rotina chamada no HTML para atualizar status das ocorrências (No wdlg chamado no não faturar).
  Caso a ocorrência estiver ativa, será desativada, e vice-versa.
*/
BEGIN


  update  w_pls_analise_glosa_ocor
  set   ie_situacao = CASE WHEN ie_situacao='A' THEN  'I'  ELSE 'A' END
  where  nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
