SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualiza_status_itens_req ( nr_seq_requisicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_define_se_gera_analise_w	varchar(1);
cd_estabelecimento_w		smallint;
ie_gerar_analise_w		varchar(1);
qt_req_audutoria_w		bigint;
ie_estagio_w			bigint;


BEGIN

select 	cd_estabelecimento,
	ie_estagio
into STRICT	cd_estabelecimento_w,
	ie_estagio_w
from	pls_requisicao
where	nr_sequencia = nr_seq_requisicao_p;

ie_define_se_gera_analise_w := pls_obter_define_gera_analise(cd_estabelecimento_w,'R');

qt_req_audutoria_w	:= pls_obter_se_req_auditoria(nr_seq_requisicao_p, '');

if (ie_define_se_gera_analise_w = 'N') then
	ie_gerar_analise_w := pls_obter_se_gerar_analise(nr_seq_requisicao_p,  null, 0);
	if (ie_gerar_analise_w = 'N') then
		qt_req_audutoria_w := 0;
	end if;
end if;

if (qt_req_audutoria_w	<> 0) then
	update	pls_requisicao_proc
	set	ie_status		= 'A',
		qt_procedimento		= 0,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	nr_sequencia	not in (SELECT	x.nr_seq_proc
					from	pls_ocorrencia_benef	x);

	update	pls_requisicao_mat
	set	ie_status		= 'A',
		qt_material		= 0,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	nr_sequencia	not in (SELECT	x.nr_seq_mat
					from	pls_ocorrencia_benef	x);
elsif (pls_obter_se_requisicao_glosa(nr_seq_requisicao_p,'R')	= 'S') then
	update	pls_requisicao_proc
	set	ie_status		= 'N',
		qt_procedimento		= 0,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

	update	pls_requisicao_mat
	set	ie_status		= 'N',
		qt_material		= 0,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
elsif (ie_estagio_w	= 10)/* and (pls_obter_se_requisicao_glosa(nr_seq_requisicao_p,'R')	= 'N') and (qt_req_audutoria_w	= 0) */
then
	update	pls_requisicao_proc
	set	ie_status		= 'I',
		qt_procedimento		= 0,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

	update	pls_requisicao_mat
	set	ie_status		= 'I',
		qt_material		= 0,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
