CREATE OR REPLACE PROCEDURE pls_atualiza_tit_desc_folha ( nr_titulo_p bigint, ie_atualiza_p text, nr_seq_conta_banco_p bigint, nr_seq_cobr_escrit_p bigint) AS $body$
DECLARE


cd_banco_w			banco_estabelecimento_v.cd_banco%type;
cd_agencia_bancaria_w		banco_estabelecimento_v.cd_agencia_bancaria%type;
cd_conta_w			banco_estabelecimento_v.cd_conta%type;
ie_digito_conta_w		banco_estabelecimento_v.ie_digito_conta%type;
nr_seq_conta_banco_w		titulo_receber.nr_seq_conta_banco%type;
nr_seq_carteira_cobr_w		titulo_receber.nr_seq_carteira_cobr%type;


BEGIN

select 	nr_seq_conta_banco,
	nr_seq_carteira_cobr
into STRICT	nr_seq_conta_banco_w,
	nr_seq_carteira_cobr_w
from 	titulo_receber
where 	nr_titulo = nr_titulo_p;

select 	max(cd_banco),
	max(cd_agencia_bancaria),
	max(cd_conta),
	coalesce(max(ie_digito_conta),'0') ie_digito_conta
into STRICT	cd_banco_w,
	cd_agencia_bancaria_w,
	cd_conta_w,
	ie_digito_conta_w
from 	banco_estabelecimento_v
where 	nr_sequencia = nr_seq_conta_banco_p;

update	titulo_receber_cobr
set	cd_banco		= cd_banco_w,
	cd_agencia_bancaria	= cd_agencia_bancaria_w,
	nr_conta		= cd_conta_w,
	ie_digito_conta		= ie_digito_conta_w
where 	1 = 1
and	((coalesce(cd_banco::text, '') = '' )
or (coalesce(nr_conta::text, '') = '' )
or (coalesce(cd_agencia_bancaria::text, '') = ''))
and 	nr_seq_cobranca = nr_seq_cobr_escrit_p;

if (ie_atualiza_p = 'S' and
	coalesce(nr_seq_conta_banco_w::text, '') = '' and
	coalesce(nr_seq_carteira_cobr_w::text, '') = '') then

	update	titulo_receber
	set	nr_seq_conta_banco	= nr_seq_conta_banco_p,
		nr_seq_carteira_cobr	= nr_seq_cobr_escrit_p
	where 	nr_titulo = nr_titulo_p
	and	coalesce(nr_seq_conta_banco::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
