CREATE OR REPLACE PROCEDURE pls_atualiza_tit_gera_bloq ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
BEGIN
	update	titulo_receber
	set	ie_tipo_titulo = '1',
        dt_emissao_bloqueto = clock_timestamp()
