CREATE OR REPLACE PROCEDURE pls_atualiza_tr_cns ( nr_seq_transmissao_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_solicitacao_w  	pls_solicitacao_cns.nr_sequencia%type;
nr_cartao_sus_w		pls_solicitacao_cns.nr_cartao_nac_sus%type;


BEGIN

update	pls_transmissao_solic_cns
set	ie_status_transmissao 	= ie_status_p,
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_transmissao_p;

 if (ie_status_p = '1') then

	select 	max(nr_seq_solicitacao)
	into STRICT 	nr_seq_solicitacao_w
	from 	pls_transmissao_solic_cns
	where 	nr_sequencia 	= nr_seq_transmissao_p;

	select	max(nr_cartao_nac_sus)
	into STRICT	nr_cartao_sus_w
	from	pls_solicitacao_cns
	where	nr_sequencia	= nr_seq_solicitacao_w;

	if (coalesce(nr_cartao_sus_w::text, '') = '') then
		update	pls_solicitacao_cns
		set  	ie_status  	= ie_status_p,
			nm_usuario   	= nm_usuario_p,
			dt_atualizacao  = clock_timestamp()
		where 	nr_sequencia 	= nr_seq_solicitacao_w;
	end if;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
