SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualiza_valores_contes ( nr_seq_contestacao_p ptu_camara_contestacao.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE


vl_total_acordo_w		ptu_quest_serv_rrs.vl_acordo_serv%type;
qt_registro_w			integer;


BEGIN
select	count(1)
into STRICT	qt_registro_w
from	ptu_questionamento
where	nr_seq_contestacao	= nr_seq_contestacao_p  LIMIT 1;

if (qt_registro_w > 0) then
	select	coalesce(sum(a.vl_acordo),0)
	into STRICT	vl_total_acordo_w
	from	ptu_questionamento a,
		ptu_camara_contestacao b
	where	a.nr_seq_contestacao	= b.nr_sequencia
	and	b.nr_sequencia 		= nr_seq_contestacao_p
	and	exists (SELECT	d.cd_motivo
			from	ptu_motivo_questionamento d,
				ptu_questionamento_codigo c
			where	c.nr_seq_registro    		= a.nr_sequencia
			and	c.nr_seq_mot_questionamento 	= d.nr_sequencia);
else
	select	coalesce(sum(c.vl_acordo_serv),0)
	into STRICT	vl_total_acordo_w
	from	ptu_quest_serv_rrs c,
		ptu_questionamento_rrs a,
		ptu_camara_contestacao b
	where	a.nr_seq_contestacao	= b.nr_sequencia
	and	a.nr_sequencia		= c.nr_seq_quest_rrs
	and	b.nr_sequencia 		= nr_seq_contestacao_p
	and	exists (SELECT	d.cd_motivo
			from	ptu_motivo_questionamento d,
				ptu_questionamento_codigo c
			where	c.nr_seq_registro    		= a.nr_sequencia
			and	c.nr_seq_mot_questionamento 	= d.nr_sequencia);
end if;

update	ptu_camara_contestacao
set	vl_total_acordo		= vl_total_acordo_w,
	nm_usuario		= nm_usuario_p,
	cd_estabelecimento	= cd_estabelecimento_p
where	nr_sequencia 		= nr_seq_contestacao_p;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
