CREATE OR REPLACE PROCEDURE pls_atualiza_valor_guia ( cd_guia_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta
	where	cd_guia	= cd_guia_p;

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_atualiza_valor_conta(nr_seq_conta_w, nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
