CREATE OR REPLACE PROCEDURE pls_atualiza_valor_item_aud ( nr_seq_item_proc_p bigint, nr_seq_item_mat_p bigint, ie_tipo_auditoria_p text, nm_usuario_p text) AS $body$
DECLARE

 
/*ie_tipo_item_p				 
 P - Procedimento 
 M- Material 
 
 ie_tipo_auditoria_p 
 A - Auditoria 
 R - Requisição  
 */
 
  
ie_permite_inf_valor_mat_w	varchar(1);


BEGIN 
 
ie_permite_inf_valor_mat_w	:= obter_valor_param_usuario(1270, 50, Obter_Perfil_Ativo, nm_usuario_p, 0);
 
 
if (coalesce(nr_seq_item_proc_p,0) > 0) then 
	CALL pls_atualiza_valor_proc_aut(nr_seq_item_proc_p,'A'||ie_tipo_auditoria_p,nm_usuario_p);
end if;
 
if (coalesce(ie_permite_inf_valor_mat_w,'X')	<> 'S') then 
	if (coalesce(nr_seq_item_mat_p,0) > 0) then 
		CALL pls_atualiza_valor_mat_aut(nr_seq_item_mat_p,'A'||ie_tipo_auditoria_p,nm_usuario_p);
	end if;
end if;
 
commit;
 
end;
$body$
