CREATE OR REPLACE PROCEDURE pls_atualiza_valor_lanc_manual ( nr_seq_mensalidade_p bigint, cd_estabelecimento_p bigint, nr_seq_lanc_manual_p bigint, NM_USUARIO_P text) AS $body$
DECLARE

 
nr_seq_tipo_lanc_w	bigint;
qt_regra_pro_rata_dia_w	bigint;
ie_operacao_motivo_w	varchar(1);
vl_item_w		pls_mensalidade_seg_item.vl_item%type;
nr_seq_lote_w		bigint;
		

BEGIN 
 
if (coalesce(nr_seq_lanc_manual_p,0) <> 0) then 
 
	select	max(nr_seq_tipo_lanc), 
		vl_item 
	into STRICT	nr_seq_tipo_lanc_w, 
		vl_item_w 
	from	pls_mensalidade_seg_item 
	where	nr_sequencia = nr_seq_lanc_manual_p 
	group by vl_item;
 
	select	max(ie_operacao_motivo) 
	into STRICT	ie_operacao_motivo_w 
	from	pls_tipo_lanc_adic 
	where	nr_sequencia = nr_seq_tipo_lanc_w;
 
	if	(((coalesce(ie_operacao_motivo_w,'S') = 'D') and (vl_item_w > 0)) or 
		 ((coalesce(ie_operacao_motivo_w,'S') = 'S') and (vl_item_w < 0))) then 
		update	pls_mensalidade_seg_item 
		set	vl_item = (vl_item * -1) 
		where	nr_sequencia = nr_seq_lanc_manual_p;
	end if;
end if;
 
CALL pls_atualizar_ato_coop_mens(nr_seq_mensalidade_p,CD_ESTABELECIMENTO_P,NM_USUARIO_P);
 
select	count(1) 
into STRICT	qt_regra_pro_rata_dia_w 
from	pls_regra_pro_rata_dia 
where	cd_estabelecimento	= cd_estabelecimento_p  LIMIT 1;
 
if (qt_regra_pro_rata_dia_w	> 0) then 
	CALL pls_gerar_pro_rata_dia(nr_seq_mensalidade_p);
end if;
 
CALL pls_atualizar_tributos_mens(nr_seq_mensalidade_p,CD_ESTABELECIMENTO_P,NM_USUARIO_P);
 
select	max(nr_seq_lote) 
into STRICT	nr_seq_lote_w 
from	pls_mensalidade 
where	nr_sequencia	= nr_seq_mensalidade_p;
 
CALL pls_atualiza_valor_mensalidade(nr_seq_lote_w,NM_USUARIO_P);
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
