	vl_material_ptu_w	:= (qt_procedimento_w		* vl_material_ptu_w);
	vl_co_ptu_w		:= (qt_procedimento_w		* vl_co_ptu_w);
	vl_medico_ptu_w		:= (qt_procedimento_w		* vl_medico_ptu_w);
	vl_total_partic_w	:= (qt_procedimento_w		* vl_total_partic_w);
	vl_auxiliares_w		:= (qt_procedimento_w		* vl_auxiliares_w);
	vl_anestesista_w	:= (qt_procedimento_w		* vl_anestesista_w);
	
	if (ie_gravar_log_p = 'S') then
		ds_observacao_log_w	:= 'Regra: '||dados_adic_horario_w.nr_sequencia||'; Tx proced: '||dados_adic_horario_w.tx_procedimento||'; Tx medico: '||dados_adic_horario_w.tx_medico||'; Tx custo: '||dados_adic_horario_w.tx_custo_operacional||'; Tx mat: '||dados_adic_horario_w.tx_materiais;
		CALL pls_gravar_log_calculo_proc(	'S', nr_sequencia_p, cd_estabelecimento_w,
						'Regra horario', ds_observacao_log_w, 'pls_atualiza_valor_proc_int',
						0, vl_material_ptu_w, vl_co_ptu_w,
						vl_total_partic_w, 0, 0,
						4, null, null,
						null, null, 'PLS_CONTA_POS_ESTABELECIDO', nm_usuario_p);
	end if;

	SELECT * FROM pls_gerar_honorario_proc( 	nr_sequencia_p, cd_procedimento_w, ie_origem_proced_w, nr_seq_grau_partic_w, ie_tipo_guia_w, nr_seq_tipo_atendimento_w, ie_tipo_conta_w, nr_seq_prestador_exec_w, ie_tipo_protocolo_w, nr_auxiliares_w, ie_status_w, ie_repassa_medico_w, vl_anestesista_w, vl_medico_ptu_w, vl_auxiliares_w, cd_estabelecimento_w, dt_procedimento_w, nm_usuario_p, nr_seq_categoria_w, dt_preco_w, nr_seq_tipo_acomodacao_w, nr_seq_clinica_w, nr_seq_plano_w, 'O', nr_seq_contrato_w, cd_convenio_w, cd_categoria_w, ie_tipo_contratacao_w, qt_dias_internacao_w, nr_seq_segurado_w, nr_seq_intercambio_w, ie_internado_w, ie_tecnica_utilizada_w, 'A', 'X', ie_carater_internacao_w, 'P', vl_total_partic_w, ie_tipo_partic_prof_w, null, null, ie_acao_p ) INTO STRICT vl_total_partic_w, ie_tipo_partic_prof_w;
	

	
	select	max(ie_nao_gera_tx_inter),
		max(ie_preco_informado)
	into STRICT	ie_nao_gera_tx_inter_w,
		ie_preco_informado_w
	from	pls_regra_preco_proc
	where	nr_sequencia	= nr_seq_regra_inter_w;
							
elsif (ie_tipo_despesa_p in ('2','3')) then /* Atualizar os valores das taxas e diarias */
	dados_regra_preco_servico_w := pls_define_preco_servico(	cd_estabelecimento_w, nr_seq_prestador_exec_w, dt_preco_w, cd_procedimento_w, ie_origem_proced_w, ie_tipo_tabela_int_w, nr_sequencia_p, ie_gravar_log_p, ie_tipo_contratacao_w, nr_seq_plano_w, '', '', '', nr_seq_contrato_w, nr_seq_classificacao_prest_w, nr_seq_categoria_w, ie_internado_w, ie_tipo_vinculo_w, nr_seq_intercambio_w, nr_seq_segurado_w, ie_tipo_intercambio_w, ie_tipo_guia_w, nr_seq_cbo_saude_w, ie_carater_internacao_w, nr_seq_clinica_w, nr_seq_tipo_atendimento_w, nr_seq_tipo_acomodacao_w, dados_prestador_prot_w, dados_conta_valor_w, dados_regra_preco_servico_w, null, cd_prestador_solic_w, ie_regime_atendimento_w, ie_saude_ocupacional_w);
	
	nr_seq_regra_inter_w		:= dados_regra_preco_servico_w.nr_sequencia;
	vl_procedimento_ptu_w 		:= dados_regra_preco_servico_w.vl_servico;
	cd_tabela_servico_w		:= dados_regra_preco_servico_w.cd_tabela_servico;
	dt_vigencia_w			:= dados_regra_preco_servico_w.dt_vigencia;
	
	select	max(ie_nao_gera_tx_inter)
	into STRICT	ie_nao_gera_tx_inter_w
	from	pls_regra_preco_servico
	where	nr_sequencia	= nr_seq_regra_inter_w;
	
elsif (ie_tipo_despesa_p = '4') then

	SELECT * FROM pls_define_preco_pacote(	cd_estabelecimento_w, nr_seq_prestador_exec_w, nr_seq_tipo_acomodacao_w, dt_preco_w, cd_procedimento_w, ie_origem_proced_w, ie_internado_w, nr_seq_plano_w, nr_contrato_w, nr_seq_congenere_w, nm_usuario_p, ie_origem_conta_w, ie_tipo_intercambio_w, nr_seq_pacote_w, nr_seq_regra_pacote_w, cd_proc_pacote_w, ie_origem_pacote_w, vl_beneficiario_w, vl_medico_w, vl_anestesista_w, vl_auxiliares_w, vl_custo_operacional_w, vl_filme_w, nr_seq_intercambio_w, nr_seq_classificacao_prest_w, nr_sequencia_p, nr_seq_segurado_w, null, 'S', null, 1) INTO STRICT nr_seq_pacote_w, nr_seq_regra_pacote_w, cd_proc_pacote_w, ie_origem_pacote_w, vl_beneficiario_w, vl_medico_w, vl_anestesista_w, vl_auxiliares_w, vl_custo_operacional_w, vl_filme_w;
				
end if;

if (ie_tipo_despesa_p in ('2','3'))or
	((ie_tipo_despesa_p = '4') and (coalesce(ie_calculo_pacote_w,'X') != 'R')) then
	-- Tratar os adicionais por horario 
	dados_adic_horario_w := pls_cta_valorizacao_pck.pls_define_adic_horario(     	dados_conta_proc_valor_w, dados_prestador_exec_w, dados_prestador_prot_w, dados_conta_valor_w, dados_procedimento_w, dados_beneficiario_valor_w, 'O', nm_usuario_p, cd_estabelecimento_w, dados_adic_horario_w);

	vl_material_ptu_w	:= (dados_adic_horario_w.tx_materiais		* vl_material_ptu_w);
	vl_co_ptu_w		:= (dados_adic_horario_w.tx_custo_operacional	* vl_co_ptu_w);
	vl_medico_ptu_w		:= (dados_adic_horario_w.tx_medico		* vl_medico_ptu_w);
	vl_total_partic_w	:= (dados_adic_horario_w.tx_medico		* vl_total_partic_w);

	if (ie_gravar_log_p = 'S') then
		ds_observacao_log_w	:= 'Regra: '||dados_adic_horario_w.nr_sequencia||'; Tx proced: '||dados_adic_horario_w.tx_procedimento||'; Tx medico: '||dados_adic_horario_w.tx_medico||'; Tx custo: '||dados_adic_horario_w.tx_custo_operacional||'; Tx mat: '||dados_adic_horario_w.tx_materiais;
		CALL pls_gravar_log_calculo_proc(	'S', nr_sequencia_p, cd_estabelecimento_w,
						'Regra horario', ds_observacao_log_w, 'pls_atualiza_valor_proc_int',
						0, vl_material_ptu_w, vl_co_ptu_w,
						vl_total_partic_w, 0, 0,
						4, null, null,
						null, null, 'PLS_CONTA_POS_ESTABELECIDO', nm_usuario_p);
	end if;

end if;
if (coalesce(ie_nao_gera_tx_inter_w,'N') = 'S') then
	tx_intercambio_w	:= 0;
end if;

if (ie_tipo_despesa_p = '4') and (coalesce(ie_calculo_pacote_w,'R') != 'R') then
	vl_procedimento_ptu_w := qt_procedimento_w * coalesce(vl_beneficiario_w,0);
	vl_beneficiario_w := vl_procedimento_ptu_w + coalesce(dividir((vl_procedimento_ptu_w*tx_intercambio_w),100),0);
		
	
	if (coalesce(tx_item_w,100) <> 100 ) then
		vl_beneficiario_w	:= dividir((coalesce(vl_beneficiario_w,0)*tx_item_w),100);
		vl_procedimento_ptu_w	:= dividir((vl_procedimento_ptu_w*tx_item_w),100);
		vl_co_ptu_w		:= qt_procedimento_w * dividir((vl_co_ptu_w*tx_item_w),100);
		vl_material_ptu_w	:= qt_procedimento_w * dividir((vl_material_ptu_w*tx_item_w),100);
	end if;
elsif	((ie_tipo_despesa_p = '1') or			/* Atualizar o valor do procedimento */
	(ie_tipo_despesa_p = '4' AND ie_calculo_pacote_w = 'R')) then		
	
	/*Calculado do valor de faturamento que deve ser a soma de co + medico + filme respeitando a regra de honorario quando se tratar de tipo de despesa procedimento*/

	
	if (ie_repassa_medico_w	= 'H') and (ie_honorario_pos_w	= 'S') then
		vl_procedimento_ptu_w	:= coalesce(vl_total_partic_w,0);
		
		vl_taxa_co_w		:= 0;
		vl_taxa_material_w	:= 0;
		vl_co_ptu_w		:= 0;
		vl_material_ptu_w	:= 0;
		
	elsif (ie_repassa_medico_w IS NOT NULL AND ie_repassa_medico_w::text <> '') then
		
		
		vl_total_partic_w	:= coalesce(vl_total_partic_w,0);
		vl_co_ptu_w		:= coalesce(vl_co_ptu_w,0);
		
		if (pls_cta_valorizacao_pck.obter_se_taxa_filme(nr_seq_prestador_exec_w, cd_estabelecimento_w) = 'S') then	
			vl_material_ptu_w := coalesce(vl_material_ptu_w,0);
		end if;
		vl_taxa_co_w		:= dividir((vl_co_ptu_w*tx_intercambio_w),100); -- OS 437002 - Conforme conversado com Paulo, valor da taxa deve ser sobre o valor total
		vl_taxa_material_w	:= dividir((vl_material_ptu_w*tx_intercambio_w),100);
		
		vl_procedimento_ptu_w	:= coalesce(vl_total_partic_w,0) + coalesce(vl_co_ptu_w,0) + coalesce(vl_material_ptu_w,0);	
	else
		vl_procedimento_ptu_w 	:= 0;
	
		vl_taxa_co_w		:= 0; -- OS 437002 - Conforme conversado com Paulo, valor da taxa deve ser sobre o valor total
		vl_taxa_material_w	:= 0;
	end if;
	
	
	vl_procedimento_ptu_w	:= (coalesce(dados_adic_horario_w.tx_procedimento,1) * vl_procedimento_ptu_w);
	vl_taxa_servico_w	:= dividir((vl_procedimento_ptu_w*tx_intercambio_w),100);
	if (ie_nao_gera_tx_inter_w	= 'F') then
		vl_taxa_servico_w	:= vl_taxa_servico_w - vl_taxa_material_w;
		vl_taxa_material_w	:= 0;
	end if;
	vl_beneficiario_w	:= (coalesce(vl_procedimento_ptu_w,0) + coalesce(vl_taxa_servico_w,0));
	
else
	vl_taxa_co_w		:= 0; -- OS 437002 - Conforme conversado com Paulo, valor da taxa deve ser sobre o valor total
	vl_taxa_material_w	:= 0;
	vl_procedimento_ptu_w	:= qt_procedimento_w * (coalesce(dados_adic_horario_w.tx_procedimento,1) * vl_procedimento_ptu_w);
	vl_taxa_servico_w	:= dividir((vl_procedimento_ptu_w*tx_intercambio_w),100);
	vl_beneficiario_w	:= (coalesce(vl_procedimento_ptu_w,0) + coalesce(vl_taxa_servico_w,0));
	
	
	if (coalesce(tx_item_w,100) <> 100 ) then
		vl_beneficiario_w	:= dividir((coalesce(vl_beneficiario_w,0)*tx_item_w),100);
		vl_procedimento_ptu_w	:= dividir((vl_procedimento_ptu_w*tx_item_w),100);
		vl_co_ptu_w		:= qt_procedimento_w * dividir((vl_co_ptu_w*tx_item_w),100);
		vl_material_ptu_w	:= qt_procedimento_w * dividir((vl_material_ptu_w*tx_item_w),100);
	end if;
end if;


update	pls_conta_proc
set	vl_beneficiario			= coalesce(vl_beneficiario_w,0),
	nr_seq_regra_int		= coalesce(nr_seq_regra_inter_w,nr_seq_regra_pacote_w),
	vl_taxa_co			= vl_taxa_co_w,
	vl_taxa_material		= vl_taxa_material_w,
	vl_taxa_servico			= vl_taxa_servico_w,
	vl_procedimento_ptu		= vl_procedimento_ptu_w,
	vl_material_ptu			= vl_material_ptu_w,
	vl_co_ptu			= vl_co_ptu_w
where	nr_sequencia			= nr_sequencia_p;

vl_beneficiario_p 					:= coalesce(vl_beneficiario_w,0);
nr_seq_regra_p	 					:= nr_seq_regra_inter_w;
dados_regra_preco_proc_p.vl_proc_tabela			:= coalesce(dados_regra_preco_proc_w.vl_proc_tabela,dados_regra_preco_servico_w.vl_servico);
dados_regra_preco_proc_p.vl_ch_honorarios		:= coalesce(dados_regra_preco_proc_w.vl_ch_honorarios,dados_regra_preco_servico_w.vl_ch_servico);
dados_regra_preco_proc_p.cd_moeda_autogerado		:= coalesce(dados_regra_preco_proc_w.cd_moeda_autogerado,dados_regra_preco_servico_w.cd_moeda);
dados_regra_preco_proc_p.vl_custo_operacional_tab 	:= dados_regra_preco_proc_w.vl_custo_operacional_tab;
dados_regra_preco_proc_p.qt_filme_tab 			:= dados_regra_preco_proc_w.qt_filme_tab;
dados_regra_preco_proc_p.vl_base_filme			:= dados_regra_preco_proc_w.vl_base_filme;
dados_regra_preco_proc_p.cd_porte_anestesico		:= dados_regra_preco_proc_w.cd_porte_anestesico;
dados_regra_preco_proc_p.ie_nao_gera_tx_inter		:= ie_nao_gera_tx_inter_w;
dados_regra_preco_proc_p.nr_seq_criterio_horario	:= dados_adic_horario_w.nr_sequencia;
dados_regra_preco_proc_p.vl_medico			:= vl_medico_ww;
dados_regra_preco_proc_p.tx_item			:= tx_item_regra_w;
dados_regra_preco_proc_p.nr_seq_regra_tx_proc_int	:= nr_seq_regra_tx_w;
tx_intercambio_p					:= tx_intercambio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualiza_valor_proc_int ( nr_sequencia_p pls_conta_proc.nr_sequencia%type, ie_tipo_despesa_p text, ie_gravar_log_p text, nm_usuario_p usuario.nm_usuario%type, tx_intercambio_p INOUT bigint, ie_pos_estab_faturamento_p pls_parametros.ie_pos_estab_faturamento%type, ie_geracao_pos_estabelecido_p pls_parametros.ie_geracao_pos_estabelecido%type, ie_preco_interc_congenere_p pls_parametros.ie_preco_interc_congenere%type, nr_seq_regra_p INOUT pls_regra_preco_proc.nr_sequencia%type, vl_beneficiario_p INOUT bigint, dados_regra_preco_proc_p INOUT pls_cta_valorizacao_pck.dados_regra_preco_proc, ie_acao_p text default 'A') FROM PUBLIC;

