CREATE OR REPLACE PROCEDURE pls_atualiza_vl_tot_proposta ( nr_seq_proposta_p bigint, ie_commit_p text) AS $body$
DECLARE

 
vl_benef_proposta_w		double precision;				
vl_proposta_w			double precision;


BEGIN 
 
vl_benef_proposta_w	:= 0;
 
SELECT * FROM pls_calcular_propos_benef(nr_seq_proposta_p, vl_benef_proposta_w, vl_proposta_w) INTO STRICT vl_benef_proposta_w, vl_proposta_w;
 
if (vl_proposta_w <> 0) then 
	update	pls_proposta_adesao 
	set	vl_proposta		= vl_proposta_w 
	where	nr_sequencia	= nr_seq_proposta_p;
end if;	
 
if (coalesce(ie_commit_p,'S') = 'S') then 
	commit;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
