CREATE OR REPLACE PROCEDURE pls_atual_anex_item_compl_web (nr_seq_item_aud_p bigint, nr_seq_requisicao_p bigint, nr_seq_item_req_p bigint, nr_registro_anvisa_p text, cd_ref_fabricante_p text, ie_via_administracao_p text, ie_opcao_fabricante_p bigint, ie_frequencia_dose_p bigint, cd_aut_funcionamento_p text, nm_usuario_p text) AS $body$
DECLARE

										 
nr_seq_item_req_w		pls_requisicao_mat.nr_sequencia%type;
nr_seq_anexo_item_w		pls_lote_anexo_mat_aut.nr_sequencia%type;
nr_seq_anexo_w			pls_lote_anexo_guias_aut.nr_sequencia%type;


BEGIN 
 
if (nr_seq_item_req_p IS NOT NULL AND nr_seq_item_req_p::text <> '') then 
	nr_seq_item_req_w := nr_seq_item_req_p;
else 
	select	max(nr_seq_mat_origem) 
	into STRICT	nr_seq_item_req_w 
	from	pls_auditoria_item a, 
			pls_auditoria b 
	where	a.nr_seq_auditoria = b.nr_sequencia 
	and		a.nr_sequencia = nr_seq_item_aud_p 
	and		b.nr_seq_requisicao = nr_seq_requisicao_p;
end if;
 
if (nr_seq_item_req_w IS NOT NULL AND nr_seq_item_req_w::text <> '') then 
 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_anexo_item_w 
	from	pls_lote_anexo_mat_aut 
	where	nr_seq_req_mat = nr_seq_item_req_w;
	 
	select max(nr_sequencia) 
	into STRICT	nr_seq_anexo_w 
	from  pls_lote_anexo_guias_aut 
	where  nr_seq_requisicao = nr_seq_requisicao_p 
	and   ie_tipo_anexo = 'OP';
	 
	if (coalesce(nr_seq_anexo_w::text, '') = '') then 
		nr_seq_anexo_w := pls_gerar_cab_anexo_req_web(nr_seq_requisicao_p, 'OP', nm_usuario_p, nr_seq_anexo_w);
	end if;
	 
	if (coalesce(nr_seq_anexo_item_w::text, '') = '') then 
		insert	into	pls_lote_anexo_mat_aut( nr_sequencia, nr_registro_anvisa, cd_ref_fabricante_imp, 
												ie_via_administracao, ie_opcao_fabricante, nr_seq_lote_anexo_guia, 
												nr_seq_req_mat, nm_usuario, nm_usuario_nrec, 
												dt_atualizacao, dt_atualizacao_nrec, ie_frequencia_dose, 
												cd_aut_funcionamento) 
										values (nextval('pls_lote_anexo_mat_aut_seq'), nr_registro_anvisa_p, cd_ref_fabricante_p, 
												ie_via_administracao_p, ie_opcao_fabricante_p, nr_seq_anexo_w, 
												nr_seq_item_req_w, nm_usuario_p, nm_usuario_p, 
												clock_timestamp(), clock_timestamp(), ie_frequencia_dose_p, 
												cd_aut_funcionamento_p);
	else 
		update	pls_lote_anexo_mat_aut 
		set		ie_via_administracao = ie_via_administracao_p, 
				nr_registro_anvisa = nr_registro_anvisa_p, 
				cd_ref_fabricante_imp = cd_ref_fabricante_p, 
				ie_opcao_fabricante = ie_opcao_fabricante_p, 
				ie_frequencia_dose = ie_frequencia_dose_p, 
				nm_usuario = nm_usuario_p, 
				dt_atualizacao = clock_timestamp(), 
				cd_aut_funcionamento = cd_aut_funcionamento_p 
		where	nr_sequencia = nr_seq_anexo_item_w;
	end if;
	 
	commit;
end if;
 
commit;
 
end;
$body$
