SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atual_auditor_grupo_atual () AS $body$
DECLARE


qt_min_desvinc_w		bigint;
nr_seq_analise_w		bigint;
cd_estabelecimento_w		bigint;
dt_inicio_auditor_w		timestamp;
nr_seq_grupo_analise_w		bigint;
nr_seq_analise_grupo_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_sequencia,
		a.dt_inicio_auditor,
		b.cd_estabelecimento
	from	pls_auditoria_conta_grupo a,
		pls_analise_conta b
	where	a.nr_seq_analise = b.nr_sequencia
	and	(a.dt_inicio_auditor IS NOT NULL AND a.dt_inicio_auditor::text <> '')
	and	coalesce(a.dt_final_analise::text, '') = ''
	order by 1,2;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_grupo_analise_w,
	nr_seq_analise_w,
	dt_inicio_auditor_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(qt_min_desvinc),0)
	into STRICT	qt_min_desvinc_w
	from	pls_param_analise_conta
	where	cd_estabelecimento = cd_estabelecimento_w;

	if (coalesce(qt_min_desvinc_w,0) > 0) and
		((dt_inicio_auditor_w + (qt_min_desvinc_w/24/60)) < clock_timestamp()) then

		update	pls_tempo_conta_grupo
		set	dt_final_analise 	= clock_timestamp(),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= 'Tasy'
		where	nr_seq_auditoria	= nr_seq_grupo_analise_w
		and	coalesce(dt_final_analise::text, '') = '';

		update	pls_auditoria_conta_grupo
		set	dt_inicio_auditor	 = NULL,
			nm_auditor_atual	 = NULL,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= coalesce(wheb_usuario_pck.get_nm_usuario,'Tasy')
		where	nr_sequencia		= nr_seq_grupo_analise_w
		and	coalesce(dt_liberacao::text, '') = '';
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
