CREATE OR REPLACE PROCEDURE pls_atual_conver_mat_item ( nr_seq_preco_item_p bigint, nr_seq_material_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_inicio_vigencia_w	timestamp;
qr_convercao_w		pls_material_valor_item.qt_convercao%type;
nr_seq_material_w	pls_material_preco_item.nr_seq_material%type;
nr_seq_preco_item_w	pls_material_preco_item.nr_sequencia%type;


BEGIN

select	max(a.DT_INICIO_VIGENCIA)
into STRICT	dt_inicio_vigencia_w
from	pls_material_valor_item a
where	a.nr_seq_preco_item	= nr_seq_preco_item_p;

select	max(qt_convercao)
into STRICT	qr_convercao_w
from	pls_material_valor_item a
where	a.nr_seq_preco_item	= nr_seq_preco_item_p
and	trunc(a.dt_inicio_vigencia,'dd')	= trunc(dt_inicio_vigencia_w,'dd');

update	pls_material
set	qt_conversao	= qr_convercao_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_material_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
