CREATE OR REPLACE PROCEDURE pls_atual_conver_mat_tabela ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_inicio_vigencia_w	timestamp;
qr_convercao_w		pls_material_valor_item.qt_convercao%type;
nr_seq_material_w	pls_material_preco_item.nr_seq_material%type;
nr_seq_preco_item_w	pls_material_preco_item.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	a.nr_seq_material,
			a.nr_sequencia
	from	pls_material_preco_item a
	where	a.nr_seq_material_preco	= nr_seq_lote_p;

BEGIN

for	r_c01_w in c01() loop
	begin

	select	max(a.DT_INICIO_VIGENCIA)
	into STRICT	dt_inicio_vigencia_w
	from	pls_material_valor_item a
	where	a.nr_seq_preco_item	= r_c01_w.nr_sequencia;

	select	max(qt_convercao)
	into STRICT	qr_convercao_w
	from	pls_material_valor_item a
	where	a.nr_seq_preco_item	= r_c01_w.nr_sequencia
	and	trunc(a.dt_inicio_vigencia,'dd')	= trunc(dt_inicio_vigencia_w,'dd');

	update	pls_material
	set	qt_conversao	= qr_convercao_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= r_c01_w.nr_seq_material;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
