SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atual_proc_mat_analise () AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_item_w		bigint;
ie_tipo_item_w		varchar(1);

C01 CURSOR FOR
	SELECT	a.nr_seq_item,
		a.ie_tipo_item,
		a.nr_sequencia
	from	w_pls_resumo_conta a,
		pls_analise_conta  b
	where	a.nr_seq_analise 	= b.nr_sequencia
	and	b.ie_origem_analise	in (1,2,3);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_item_w,
	ie_tipo_item_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	begin
	if (ie_tipo_item_w = 'P') then
		update	w_pls_resumo_conta
		set	nr_seq_conta_proc 	= nr_seq_item_w
		where	nr_sequencia 		= nr_sequencia_w;
	elsif (ie_tipo_item_w = 'M') then
		update	w_pls_resumo_conta
		set	nr_seq_conta_mat 	= nr_seq_item_w
		where	nr_sequencia 		= nr_sequencia_w;
	end if;
	exception
	when others then
		null;
	end;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
