SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ausencia_operador_popup (nr_seq_operador_p bigint, ds_horas_p text, nr_seq_motivo_p bigint, nm_usuario_p text) AS $body$
DECLARE

  dt_entrada_w      timestamp;
  dt_saida_w        timestamp;
  nr_seq_ausencia_w pls_atendimento_ausencia.nr_sequencia%type;
  tipo_evento_w     pls_atend_motivo_ausencia.ie_tipo_evento%type;

BEGIN
    select coalesce(max(a.ie_tipo_evento), 0)
      into STRICT tipo_evento_w
      from pls_atend_motivo_ausencia a
     where a.nr_sequencia = nr_seq_motivo_p;

    select coalesce(max(a.dt_entrada), clock_timestamp())
      into STRICT dt_entrada_w
      from pls_operador_controle a
     where a.nr_sequencia =
           (SELECT max(b.nr_sequencia)
              from pls_operador_controle b
             where b.nr_seq_operador = nr_seq_operador_p);

  dt_saida_w := to_date(to_char(dt_entrada_w, 'dd/mm/yyyy') || ' ' ||
                        ds_horas_p || ':00',
                        'dd/mm/yyyy hh24:mi:ss');
  if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') then
    select nextval('pls_atendimento_ausencia_seq')
      into STRICT nr_seq_ausencia_w
;
    insert into pls_atendimento_ausencia(nr_sequencia,
       nr_seq_motivo,
       dt_inicio,
       dt_atualizacao,
       nm_usuario,
       dt_atualizacao_nrec,
       nm_usuario_nrec,
       nr_seq_operador,
       cd_estabelecimento)
    values (nr_seq_ausencia_w,
       nr_seq_motivo_p,
       dt_saida_w,
       clock_timestamp(),
       nm_usuario_p,
       clock_timestamp(),
       nm_usuario_p,
       nr_seq_operador_p,
       wheb_usuario_pck.get_cd_estabelecimento);
  end if;

  update pls_operador_controle
     set dt_saida         = dt_saida_w,
         dt_atualizacao   = clock_timestamp(),
         nm_usuario       = nm_usuario_p,
         ie_pausa_central = CASE WHEN tipo_evento_w=1 THEN  'N' WHEN tipo_evento_w=2 THEN  'S'  ELSE null END ,
         nr_seq_ausencia  = nr_seq_ausencia_w
   where nr_seq_operador = nr_seq_operador_p
     and coalesce(dt_saida::text, '') = '';
  commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
