CREATE OR REPLACE PROCEDURE pls_autoriza_guia_analise ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
dt_solicitacao_w		timestamp;
ie_tipo_guia_w			varchar(2);
dt_validade_senha_w		varchar(25);
cd_senha_w			varchar(20);
qt_dias_validade_w		bigint;


BEGIN

select	nr_seq_segurado,
	dt_solicitacao,
	ie_tipo_guia
into STRICT	nr_seq_segurado_w,
	dt_solicitacao_w,
	ie_tipo_guia_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;

qt_dias_validade_w	:= (obter_valor_param_usuario(1204, 3, Obter_Perfil_Ativo, nm_usuario_p, 0))::numeric;

SELECT * FROM pls_gerar_validade_senha(	nr_seq_guia_p, nr_seq_segurado_w, qt_dias_validade_w, dt_solicitacao_w, ie_tipo_guia_w, nm_usuario_p, dt_validade_senha_w, cd_senha_w) INTO STRICT dt_validade_senha_w, cd_senha_w;
				
update	pls_guia_plano
set	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp(),
	dt_autorizacao		= clock_timestamp(),
	cd_senha		= cd_senha_w,
	dt_validade_senha	= to_date(dt_validade_senha_w, 'dd/mm/rrrr'),
	ie_status		= 1,	
	dt_liberacao		= clock_timestamp(),
	nm_usuario_liberacao	= nm_usuario_p
where	nr_sequencia		= nr_seq_guia_p;

CALL pls_atualizar_estagio_guia(nr_seq_guia_p,nm_usuario_p,null);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
