SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_acerto_desc_analise () AS $body$
DECLARE

 
nm_cooperativa_w		varchar(255);
cd_cooperativa_w		varchar(10);
nm_segurado_w			varchar(255);
nm_prestador_w			varchar(255);
nr_seq_analise_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia,		 
		substr(pls_obter_dados_segurado(nr_seq_segurado,'N'),1,255), 
		substr(pls_obter_dados_prestador(nr_seq_prestador,'N'),1,255), 
		substr(pls_obter_nome_congenere(nr_seq_congenere),1,255), 
		substr(pls_obter_seq_codigo_coop(nr_seq_congenere,''),1,10)	 
	from	pls_analise_conta 
	where	((coalesce(nm_segurado::text, '') = '') 	 and (nr_seq_segurado IS NOT NULL AND nr_seq_segurado::text <> '')) 
	or	((coalesce(nm_cooperativa::text, '') = '')and (nr_seq_congenere IS NOT NULL AND nr_seq_congenere::text <> '')) 
	or 	((coalesce(nm_prestador::text, '') = '') and (nr_seq_prestador IS NOT NULL AND nr_seq_prestador::text <> ''));


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_analise_w, 
	nm_segurado_w, 
	nm_prestador_w, 
	nm_cooperativa_w,	 
	cd_cooperativa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		 
	update	pls_analise_conta 
	set	nm_cooperativa = nm_cooperativa_w, 
		cd_cooperativa = cd_cooperativa_w, 
		nm_segurado  = nm_segurado_w, 
		nm_prestador  = nm_prestador_w 
	where	nr_sequencia  = nr_seq_analise_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
