SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_acerto_end_com_pag () AS $body$
DECLARE


cd_pessoa_fisica_w		bigint;
nr_seq_compl_pf_tel_adic_w	bigint;
nr_seq_pagador_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_pessoa_fisica
	from	pls_contrato_pagador
	where	ie_endereco_boleto	= 'PFC2'
	and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_pagador_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_compl_pf_tel_adic_w
	from	compl_pf_tel_adic
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

	update	pls_contrato_pagador
	set	ie_endereco_boleto		= 'PFC',
		nr_seq_compl_pf_tel_adic	= nr_seq_compl_pf_tel_adic_w
	where	nr_sequencia			= nr_seq_pagador_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
