SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_acerto_lanc_manual () AS $body$
DECLARE


nr_seq_lancamento_w	bigint;
nr_seq_item_mens_w	bigint;
nr_seq_mensalidade_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_mensalidade_item
	from	pls_lancamento_mensalidade
	where	coalesce(nr_seq_mensalidade::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_lancamento_w,
	nr_seq_item_mens_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_item_mens_w IS NOT NULL AND nr_seq_item_mens_w::text <> '') then
		select	b.nr_seq_mensalidade
		into STRICT	nr_seq_mensalidade_w
		from	pls_mensalidade_seg_item	a,
			pls_mensalidade_segurado	b
		where	a.nr_seq_mensalidade_seg	= b.nr_sequencia
		and	a.nr_sequencia			= nr_seq_item_mens_w;

		update	pls_lancamento_mensalidade
		set	nr_seq_mensalidade	= nr_seq_mensalidade_w,
			ie_situacao		= 'A'
		where	nr_sequencia		= nr_seq_lancamento_w;
	else
		select	max(c.nr_sequencia)
		into STRICT	nr_seq_mensalidade_w
		from	pls_mensalidade_seg_item	a,
			pls_mensalidade_segurado	b,
			pls_mensalidade			c
		where	a.nr_seq_mensalidade_seg	= b.nr_sequencia
		and	b.nr_seq_mensalidade		= c.nr_sequencia
		and	coalesce(c.ie_cancelamento::text, '') = ''
		and	a.nr_seq_lancamento_mens	= nr_seq_lancamento_w;

		if (nr_seq_mensalidade_w IS NOT NULL AND nr_seq_mensalidade_w::text <> '') then
			update	pls_lancamento_mensalidade
			set	nr_seq_mensalidade	= nr_seq_mensalidade_w,
				ie_situacao		= 'A'
			where	nr_sequencia		= nr_seq_lancamento_w;
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
