SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_acerto_lote_prot_venc (cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
/* Baca utilizado para gerar os vencimentos do lote de protocolo através dos títulos*/
 
 
nr_seq_lote_w		bigint;
nr_titulo_w		bigint;
vl_titulo_w		double precision;
dt_vencimento_atual_w	timestamp;
nr_seq_venc_w		bigint;
qt_registro_w		bigint	:= 0;

c01 CURSOR FOR 
SELECT	a.nr_sequencia 
from	pls_lote_protocolo a 
where	not exists (SELECT	1 
			from	pls_lote_protocolo_venc x 
			where	x.nr_seq_lote	= a.nr_sequencia) 
and	a.cd_estabelecimento	= cd_estabelecimento_p;

c02 CURSOR FOR 
SELECT	a.nr_titulo, 
	a.vl_titulo, 
	a.dt_vencimento_atual 
from	titulo_pagar a 
where	a.nr_seq_lote_res_pls	= nr_seq_lote_w 
and	a.ie_tipo_titulo	<> '4';


BEGIN 
 
open c01;
loop 
fetch c01 into	 
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	open c02;
	loop 
	fetch c02 into	 
		nr_titulo_w, 
		vl_titulo_w, 
		dt_vencimento_atual_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		 
		select	nextval('pls_lote_protocolo_venc_seq') 
		into STRICT	nr_seq_venc_w 
		;
		 
		insert	into	pls_lote_protocolo_venc(nr_sequencia, 
			nm_usuario, 
			dt_atualizacao, 
			nm_usuario_nrec, 
			dt_atualizacao_nrec, 
			nr_seq_lote, 
			dt_vencimento, 
			vl_vencimento, 
			nr_titulo, 
			vl_ir, 
			pr_ir, 
			pr_imposto_munic, 
			vl_imposto_munic, 
			vl_liquido) 
		values (nr_seq_venc_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nr_seq_lote_w, 
			dt_vencimento_atual_w, 
			vl_titulo_w, 
			nr_titulo_w, 
			0, 
			0, 
			0, 
			0, 
			vl_titulo_w);
			 
		CALL pls_gerar_trib_venc_lote_pag(nr_seq_venc_w,nm_usuario_p);
		qt_registro_w	:= qt_registro_w + 1;
		if (qt_registro_w > 200) then 
			qt_registro_w	:= 0;
			commit;
		end if;
		 
		end;
	end loop;
	close c02;
	 
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
