SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_cbo_medico () AS $body$
DECLARE


cd_medico_w		varchar(10);
nr_seq_partic_w		bigint;
nr_seq_cbo_saude_w	bigint;
cd_cbo_w		varchar(10);

C01 CURSOR FOR
	SELECT	coalesce(b.cd_medico,b.cd_medico_imp),
		b.nr_sequencia
	from	pls_proc_participante	b,
		pls_conta_proc		a
	where	a.nr_sequencia		= b.nr_seq_conta_proc
	and	a.dt_procedimento	> '31/12/2007'
	and	coalesce(b.nr_seq_cbo_saude::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	cd_medico_w,
	nr_seq_partic_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then
		select	coalesce(max(nr_seq_cbo_saude),0)
		into STRICT	nr_seq_cbo_saude_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_medico_w;
	end if;

	if (nr_seq_cbo_saude_w	> 0) then
		select	coalesce(max(cd_cbo),'0')
		into STRICT	cd_cbo_w
		from	cbo_saude
		where	nr_sequencia	= nr_seq_cbo_saude_w;

		update	pls_proc_participante
		set	nr_seq_cbo_saude	= nr_seq_cbo_saude_w,
			cd_cbo_saude_imp	= cd_cbo_w
		where	nr_sequencia		= nr_seq_partic_w
		and	coalesce(nr_seq_cbo_saude::text, '') = '';
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
