SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_cd_brasindice () AS $body$
DECLARE


cd_material_w		bigint;
cd_apresentacao_w	varchar(6);
cd_laboratorio_w	varchar(6);
cd_medicamento_w	varchar(6);
cd_tiss_brasindice_w	varchar(15);
ds_medicamento_w	varchar(255);
qt_commit_w		integer := 0;
qt_material_w		integer;

C01 CURSOR FOR
	SELECT	a.cd_material,
		a.cd_apresentacao,
		a.cd_laboratorio,
		a.cd_medicamento
	from	material_brasindice a
	where	exists (SELECT	1
			from	pls_material b
			where 	a.cd_material = b.cd_material);


BEGIN
open C01;
loop
fetch C01 into
	cd_material_w,
	cd_apresentacao_w,
	cd_laboratorio_w,
	cd_medicamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_material_w
	from	material_brasindice
	where	cd_material = cd_material_w;

	if (qt_material_w = 1) then
		qt_commit_w := qt_commit_w + 1;

		select	ds_medicamento
		into STRICT	ds_medicamento_w
		from	brasindice_medicamento
		where	cd_medicamento = cd_medicamento_w;

		select	substr(tiss_obter_cod_brasindice(cd_apresentacao_w, cd_laboratorio_w, cd_medicamento_w),1,15)
		into STRICT	cd_tiss_brasindice_w
		;

		update 	pls_material
		set	cd_tiss_brasindice	= cd_tiss_brasindice_w,
			ds_medicamento		= ds_medicamento_w,
			dt_atualizacao		= clock_timestamp()
		where	cd_material		= cd_material_w;

		if (qt_commit_w >= 200) then
			commit;
			qt_commit_w := 0;
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
