SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_conta_item () AS $body$
DECLARE


nr_sequencia_nf_w		bigint;
nr_item_nf_w			bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_estabelecimento_w		smallint;
cd_operacao_nf_w		smallint;
cd_conta_financ_w		bigint;
ie_origem_titulo_w		varchar(10);
nr_seq_classe_tit_rec_w		bigint;
cd_local_estoque_w		smallint;
ie_tipo_titulo_rec_w		titulo_receber.ie_tipo_titulo%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_item_nf,
		a.cd_procedimento,
		a.ie_origem_proced,
		b.cd_estabelecimento,
		b.cd_operacao_nf,
		a.cd_local_estoque
	from	nota_fiscal_item a,
		nota_fiscal b,
		pls_mensalidade c
	where	a.nr_sequencia	= b.nr_sequencia
	and	c.nr_sequencia	= b.nr_seq_mensalidade
	and	coalesce(a.nr_seq_conta_financ::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_nf_w,
	nr_item_nf_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	cd_estabelecimento_w,
	cd_operacao_nf_w,
	cd_local_estoque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(a.ie_origem_titulo),
		max(a.nr_seq_classe),
		max(a.ie_tipo_titulo)
	into STRICT	ie_origem_titulo_w,
		nr_seq_classe_tit_rec_w,
		ie_tipo_titulo_rec_w
	from	titulo_receber a
	where	a.nr_seq_nf_saida	= nr_sequencia_nf_w;

	cd_conta_financ_w := obter_conta_financeira(	'E', cd_estabelecimento_w, null, cd_procedimento_w, ie_origem_proced_w, null, null, null, null, cd_conta_financ_w, null, cd_operacao_nf_w, null, null, null, null, null, null, ie_origem_titulo_w, null, nr_seq_classe_tit_rec_w, null, cd_local_estoque_w, null, null, null, null, ie_tipo_titulo_rec_w, null);

	if (coalesce(cd_conta_financ_w,0) <> 0) then
		update	nota_fiscal_item
		set	nr_seq_conta_financ	= cd_conta_financ_w
		where	nr_sequencia		= nr_sequencia_nf_w
		and	nr_item_nf		= nr_item_nf_w;
	end if;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
