CREATE OR REPLACE PROCEDURE pls_baca_ajustar_conta_ptu_cob () AS $body$
DECLARE


nr_seq_nota_cobr_w	bigint;

C01 CURSOR FOR
	SELECT	z.nr_sequencia
	from	ptu_nota_cobranca z,
		ptu_fatura x
	where	x.nr_sequencia	= z.nr_seq_fatura
	and	(x.nr_seq_pls_fatura IS NOT NULL AND x.nr_seq_pls_fatura::text <> '');


BEGIN
begin
open C01;
loop
fetch C01 into
	nr_seq_nota_cobr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	ptu_nota_cobranca
	set	nr_seq_conta	= nr_nota
	where	nr_sequencia	= nr_seq_nota_cobr_w;
	end;
end loop;
close C01;
exception
when others then
	null;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
