CREATE OR REPLACE PROCEDURE pls_baca_ajustar_contr_mens () AS $body$
DECLARE


nr_seq_mens_segurado_w			bigint;
nr_seq_segurado_w			bigint;
nr_seq_contrato_w			bigint;
nr_parcela_contrato_w			bigint;
dt_mesano_referencia_w			timestamp;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_segurado,
		b.nr_seq_contrato,
		a.dt_mesano_referencia
	from	pls_mensalidade_segurado a,
		pls_segurado b
	where	a.nr_seq_segurado	= b.nr_sequencia
	and	coalesce(a.nr_seq_contrato::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mens_segurado_w,
	nr_seq_segurado_w,
	nr_seq_contrato_w,
	dt_mesano_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	pls_obter_parcela_contrato(nr_seq_contrato_w,dt_mesano_referencia_w)
	into STRICT	nr_parcela_contrato_w
	;

	update	pls_mensalidade_segurado
	set	nr_seq_contrato	=	nr_seq_contrato_w,
		nr_parcela_contrato	= nr_parcela_contrato_w
	where	nr_sequencia	=	nr_seq_mens_segurado_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
