CREATE OR REPLACE PROCEDURE pls_baca_ajustar_desc_benef ( nr_seq_contrato_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_seq_segurado_preco_w		bigint;
vl_preco_atual_w		double precision;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	coalesce(dt_rescisao::text, '') = ''
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_segurado_preco_w
	from	pls_segurado_preco
	where	nr_seq_segurado = nr_seq_segurado_w;

	if (nr_seq_segurado_preco_w <> 0) then
		select	vl_preco_atual
		into STRICT	vl_preco_atual_w
		from	pls_segurado_preco
		where	nr_sequencia	= nr_seq_segurado_preco_w;

		CALL pls_gerar_desc_beneficiario(nr_seq_segurado_preco_w, vl_preco_atual_w, nm_usuario_p, cd_estabelecimento_p, 'S');
	end if;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
