CREATE OR REPLACE PROCEDURE pls_baca_ajustar_grupo_relat () AS $body$
DECLARE


ie_grupo_w		bigint;
nr_seq_grupo_relat_w	bigint;

C01 CURSOR FOR
	SELECT	ie_grupo
	from	pls_regra_relat_grupo
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_grupo_relatorio
	where	cd_grupo	= ie_grupo_w;


BEGIN

open C01;
loop
fetch C01 into
	ie_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_grupo_relat_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		update	pls_regra_relat_grupo
		set	nr_seq_grupo_relat	= nr_seq_grupo_relat_w
		where	ie_grupo		= ie_grupo_w;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
