SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_naveg_permit () AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Popular os campos ie_internet_explorer, _ie_firefox, ie_safari e ie_chrome
de acordo com o campo ie_navegador, que será desabilitado
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_web_param_geral_w   	pls_web_param_geral.nr_sequencia%type;
ie_firefox_w				pls_web_param_geral.ie_firefox%type;
ie_chrome_w					pls_web_param_geral.ie_chrome%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
			ie_navegador
	from	pls_web_param_geral;

BEGIN
for r_c01_w in C01 loop
	begin
		ie_firefox_w	:= 'N';
		ie_chrome_w		:= 'N';

		if (r_c01_w.ie_navegador = 'IF') then
			ie_firefox_w	:= 'S';
		elsif (r_c01_w.ie_navegador = 'IFC') then
			ie_firefox_w	:= 'S';
			ie_chrome_w		:= 'S';
		end if;

		update	pls_web_param_geral
		set		ie_internet_explorer	= 'S',
				ie_firefox				= ie_firefox_w,
				ie_chrome				= ie_chrome_w,
				ie_safari				= 'N'
		where	nr_sequencia			= r_c01_w.nr_sequencia;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_ajustar_naveg_permit () FROM PUBLIC;

