SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_oc_partic () AS $body$
DECLARE


nr_seq_regra_oc_w	bigint;
ie_medico_w		varchar(2);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(ie_medico,'A')
	from	pls_ocorrencia_regra
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_oc_w,
	ie_medico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_medico_w = 'A') then

		update	pls_ocorrencia_regra
		set	ie_medico_cooperado_solic 	= ie_medico_cooperado,
			cd_medico_executor_solic  	= cd_medico_executor,
			nr_seq_conselho_solic		= nr_seq_conselho,
			nr_seq_proc_espec_solic		= nr_seq_proc_espec,
			ie_medico			= 'S'
		where	nr_sequencia = nr_seq_regra_oc_w;

	elsif (ie_medico_w = 'S') then

		update	pls_ocorrencia_regra
		set	ie_medico_cooperado_solic 	= ie_medico_cooperado,
			cd_medico_executor_solic  	= cd_medico_executor,
			nr_seq_conselho_solic		= nr_seq_conselho,
			nr_seq_proc_espec_solic		= nr_seq_proc_espec,
			ie_medico			= 'N',
			ie_medico_cooperado		= 'A',
			cd_medico_executor		 = NULL,
			nr_seq_conselho			 = NULL,
			nr_seq_proc_espec		 = NULL
		where	nr_sequencia = nr_seq_regra_oc_w;

	elsif (ie_medico_w = 'E') then

		update	pls_ocorrencia_regra
		set	ie_medico			= 'N'
		where	nr_sequencia = nr_seq_regra_oc_w;

	end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
