CREATE OR REPLACE PROCEDURE pls_baca_ajustar_plano_prest ( nm_usuario_p text) AS $body$
DECLARE


ie_situacao_w			varchar(1)	:= 'A';
nr_seq_plano_w			bigint	:= null;
nr_seq_prestador_w		bigint	:= null;
qt_plano_w			bigint	:= 0;
qt_prestador_w			bigint	:= 0;

C01 CURSOR FOR
	SELECT	ie_situacao,
		nr_seq_plano,
		nr_seq_prestador
	from	pls_plano_prestador;


BEGIN
open C01;
loop
fetch C01 into
	ie_situacao_w,
	nr_seq_plano_w,
	nr_seq_prestador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_plano_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_w;

	select	count(*)
	into STRICT	qt_prestador_w
	from	pls_prestador
	where	nr_sequencia	= nr_seq_prestador_w;

	if (qt_plano_w > 0) and (qt_prestador_w > 0 ) then
		insert into pls_prestador_plano(	nr_sequencia,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_prestador,
				nr_seq_plano,
				ie_situacao,
				dt_atualizacao,
				ie_permite)
		values (	nextval('pls_prestador_plano_seq'),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_prestador_w,
				nr_seq_plano_w,
				ie_situacao_w,
				clock_timestamp(),
				'S');
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
