CREATE OR REPLACE PROCEDURE pls_baca_ajustar_regra_cartao () AS $body$
DECLARE


nr_seq_regra_campo_w		bigint;
nr_seq_regra_carteira_w		bigint;

C01 CURSOR FOR
	SELECT	c.nr_sequencia,
		b.nr_sequencia
	from	pls_emissor_carteira	a,
		pls_regra_carteira	b,
		pls_regra_carteira_campo c
	where	b.nr_seq_emissor	= a.nr_sequencia
	and	c.nr_seq_emissor	= a.nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_carteira;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_campo_w,
	nr_seq_regra_carteira_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_regra_carteira_campo
	set	nr_seq_regra_carteira	= nr_seq_regra_carteira_w
	where	nr_sequencia		= nr_seq_regra_campo_w;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_regra_carteira_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	update	pls_regra_carteira
	set	ie_tipo_estipulante	= 'A'
	where	nr_sequencia		= nr_seq_regra_carteira_w;
	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
