SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_sit_cooperado ( nm_usuario_p text) AS $body$
DECLARE


qt_situacao_ativo_w	bigint;
qt_situacao_susp_w	bigint;
qt_situacao_inativo_w	bigint;
nr_seq_cooperado_w	bigint;
nr_seq_situacao_w	bigint;
ie_status_w		varchar(2);
cd_estabelecimento_w	bigint;
cd_estab_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_estabelecimento
	from	pls_cooperado
	where	coalesce(nr_seq_situacao,0)	= 0;

C02 CURSOR FOR
	SELECT	cd_estabelecimento
	from	pls_cooperado
	group by cd_estabelecimento;


BEGIN

open C02;
loop
fetch C02 into
	cd_estab_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	select	count(*)
	into STRICT	qt_situacao_ativo_w
	from	pls_situacao_cooperado
	where	ie_status	= 'A'
	and	ie_situacao	= 'A'
	and	cd_estabelecimento	= cd_estab_w;

	if (qt_situacao_ativo_w = 0) then
		insert into pls_situacao_cooperado(	nr_sequencia, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec, ie_status,
						cd_estabelecimento, ie_situacao, ds_situacao)
				values (	nextval('pls_situacao_cooperado_seq'), clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p, 'A',
						cd_estab_w, 'A', 'Ativo');
	end if;

	select	count(*)
	into STRICT	qt_situacao_ativo_w
	from	pls_situacao_cooperado
	where	ie_status	= 'I'
	and	ie_situacao	= 'A'
	and	cd_estabelecimento	= cd_estab_w;

	if (qt_situacao_ativo_w = 0) then
		insert into pls_situacao_cooperado(	nr_sequencia, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec, ie_status,
						cd_estabelecimento, ie_situacao, ds_situacao)
				values (	nextval('pls_situacao_cooperado_seq'), clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p, 'I',
						cd_estab_w, 'A', 'Inativo');
	end if;

	select	count(*)
	into STRICT	qt_situacao_ativo_w
	from	pls_situacao_cooperado
	where	ie_status	= 'S'
	and	ie_situacao	= 'A'
	and	cd_estabelecimento	= cd_estab_w;

	if (qt_situacao_ativo_w = 0) then
		insert into pls_situacao_cooperado(	nr_sequencia, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec, ie_status,
						cd_estabelecimento, ie_situacao, ds_situacao)
				values (	nextval('pls_situacao_cooperado_seq'), clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p, 'S',
						cd_estab_w, 'A', 'Suspenso');
	end if;
	end;
end loop;
close C02;

open C01;
loop
fetch C01 into
	nr_seq_cooperado_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	ie_status
	into STRICT	ie_status_w
	from	pls_cooperado
	where	nr_sequencia = nr_seq_cooperado_w;

	select	max(nr_sequencia)
	into STRICT	nr_seq_situacao_w
	from	pls_situacao_cooperado
	where	ie_status	= ie_status_w
	and	cd_estabelecimento	= cd_estabelecimento_w;

	update	pls_cooperado
	set	nr_seq_situacao	=  nr_seq_situacao_w
	where	nr_sequencia = nr_seq_cooperado_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
