CREATE OR REPLACE PROCEDURE pls_baca_ajustar_status_guia () AS $body$
DECLARE

 
nr_seq_guia_w			bigint;
cd_estabelecimento_w		bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.cd_estabelecimento 
	from	pls_guia_plano a 
	where	a.ie_estagio in (2,3) 
	and		exists (SELECT	1 
					from	pls_guia_plano_proc x 
					where	a.nr_sequencia = x.nr_seq_guia 
					and		x.ie_status = 'U') 
	and		(a.nr_seq_segurado IS NOT NULL AND a.nr_seq_segurado::text <> '');


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_guia_w, 
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	CALL pls_consistir_proc_guia(nr_seq_guia_w, cd_estabelecimento_w, 'TASY');
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
