CREATE OR REPLACE PROCEDURE pls_baca_ajustar_tabela_sca () AS $body$
DECLARE


nr_seq_tabela_w		bigint;
nr_seq_contrato_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_tabela,
		b.nr_seq_contrato
	from	pls_sca_vinculo	a,
		pls_segurado	b,
		pls_tabela_preco c
	where	a.nr_seq_segurado	= b.nr_sequencia
	and	a.nr_seq_tabela		= c.nr_sequencia
	and	coalesce(c.nr_contrato::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_tabela_w,
	nr_seq_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_tabela_preco
	set	nr_contrato	= nr_seq_contrato_w
	where	nr_sequencia	= nr_seq_tabela_w
	and	coalesce(nr_contrato::text, '') = '';
	end;
end loop;
close C01;

commit;

end;
