SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_tab_segurado () AS $body$
DECLARE


nr_seq_tabela_w		bigint;
dt_contrato_w		timestamp;

c01 CURSOR FOR
	SELECT	distinct
		trunc(c.dt_contrato),
		b.nr_sequencia
	from	pls_segurado a,
		pls_tabela_preco b,
		pls_contrato c
	where	a.nr_sequencia = b.nr_segurado
	and	c.nr_sequencia = a.nr_seq_contrato
	and	(b.nr_segurado IS NOT NULL AND b.nr_segurado::text <> '')
	and	trunc(c.dt_contrato) <> trunc(b.dt_inicio_vigencia);


BEGIN

open c01;
loop
fetch c01 into	dt_contrato_w,
		nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	pls_tabela_preco
	set	dt_inicio_vigencia	= dt_contrato_w
	where	nr_sequencia		= nr_seq_tabela_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
