SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_valor_mens () AS $body$
DECLARE

 
nr_seq_lote_w			bigint;
nr_seq_mensalidade_seg_w	bigint;
ds_erro_w			varchar(32000);

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_lote_mensalidade a 
	where	exists (SELECT	1 
			from	pls_mensalidade x 
			where	a.nr_sequencia	= x.nr_seq_lote 
			and	coalesce(x.vl_pre_estabelecido::text, '') = '');

C02 CURSOR FOR 
	SELECT	b.nr_sequencia 
	from	pls_mensalidade	a, 
		pls_mensalidade_segurado b 
	where	a.nr_sequencia	= b.nr_seq_mensalidade 
	and	a.nr_seq_lote	= nr_seq_lote_w;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	open C02;
	loop 
	fetch C02 into	 
		nr_seq_mensalidade_seg_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		begin 
		CALL pls_atualiza_valor_mens_seg(nr_seq_mensalidade_seg_w,'TASY');
		exception 
		when others then 
			ds_erro_w	:= ds_erro_w || ', ' || nr_seq_mensalidade_seg_w;
		end;
		end;
	end loop;
	close C02;
 
	CALL pls_atualiza_valor_mensalidade(nr_seq_lote_w,'TASY');
	 
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
