CREATE OR REPLACE PROCEDURE pls_baca_ajustar_valor_param23 () AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar o valor do parâmetro [23]  - Permitir o auditor analisar somente itens pendentes e negados, no parâmetro [72] - Status dos itens que poderão ser analisados pelo auditor
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_parametro_w	varchar(1);


BEGIN

begin
	select	coalesce(vl_parametro,vl_parametro_padrao)
	into STRICT	vl_parametro_w
	from	funcao_parametro
	where	cd_funcao	= 1270
	and	nr_sequencia	= 23;
exception
when others then
	vl_parametro_w	:= 'N';
end;

if (vl_parametro_w	= 'N') then
	update	funcao_parametro
	set	vl_parametro	= 1
	where	cd_funcao	= 1270
	and	nr_sequencia	= 72;
else
	update	funcao_parametro
	set	vl_parametro	= 3
	where	cd_funcao	= 1270
	and	nr_sequencia	= 72;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
