SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_ajustar_vl_neg_filme () AS $body$
DECLARE

 
qt_commit_w	integer;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_regra_preco_proc 
	where	vl_materiais > 0 
	and	coalesce(vl_proc_negociado,0) < 
		(coalesce(vl_medico,0) + coalesce(vl_auxiliares,0) + coalesce(vl_anestesista,0) + coalesce(vl_custo_operacional,0) + coalesce(vl_materiais,0));

BEGIN 
qt_commit_w := 0;
for r_C01_w in C01() loop 
	update	pls_regra_preco_proc 
	set	vl_proc_negociado	= coalesce(vl_medico,0) + coalesce(vl_auxiliares,0) + coalesce(vl_anestesista,0) + coalesce(vl_custo_operacional,0) + coalesce(vl_materiais,0) 
	where	nr_sequencia		= r_C01_w.nr_sequencia;
	 
	qt_commit_w := qt_commit_w + 1;
	 
	if (qt_commit_w = 3000) then 
		qt_commit_w := 1;
		commit;
	end if;
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
